import {useCallback, useEffect, useState} from "react";
import {RepoService} from "../../services/RepoService";
import {Repo} from "./Repo";
import s from "./style.module.css"
import {AddRepo} from "../AddRepo";


export const Repositories = () => {
    const [repos, setRepos] = useState([]);
    const [addRepo, setAddRepo] = useState(false);

    useEffect(() => {
        (async () => {
            const data = await RepoService.getRepos();
            setRepos(data);
        })();
    }, [])

    const addRepoHandle = useCallback(() => {
        setAddRepo(true);
    }, []);

    return <div className={s.wrapper}>
        <div className={s.repositories}>
            <AddRepo visible={addRepo} setVisible={setAddRepo}/>
            <button className={s['add-button']} onClick={addRepoHandle}>Добавить</button>
            {repos.map(r => <Repo key={r.id} {...r} repos={repos} setRepos={setRepos}/>)}


        </div>
    </div>
}
