import s from "./style.module.css";
import {RepoService} from "../../../services/RepoService";
import {UpdateRepo} from "../UpdateRepo";
import {useCallback, useState} from "react";

export const Repo = ({id, name, repos, setRepos}) => {

    const [updateRepo, setUpdateRepo] = useState(false);

    const updateRepoHandle = useCallback(() => {
        setUpdateRepo(true);
    },[]);
    const handleDelete = async () => {
        await RepoService.deleteRepo(id);
        setRepos(repos.filter(r => r.id !== id));
    }

    return <div className={s.repo}>
        <div className={s.delete} onClick={handleDelete}>x</div>
        <div className={s.name}>{name}</div>
        <UpdateRepo id={id} setVisible={setUpdateRepo} visible={updateRepo}>*</UpdateRepo>
    </div>
}
