import s from "./style.module.css";

export const AuthBlock = ({
                              username,
                              password,
                              handleChangeLogin,
                              handleChangePassword,
                              handleClick,
                              responseBlock,
                              title
                          }) => {
    return <div
        className={s.wrapper}>
        <div className={s['auth-block']}>
            <h2>{title}</h2>
            <label><span>Логин:</span> <input type="text" value={username} onChange={handleChangeLogin}/></label>
            <label><span>Пароль:</span> <input type="password" value={password}
                                                 onChange={handleChangePassword}/></label>
            <button onClick={handleClick}>{title}</button>
            {responseBlock}
        </div>
    </div>
}
